import {
  bundle
} from "./chunk-S4JWBGZK.js";

// src/cli.ts
import { cac } from "cac";

// package.json
var version = "0.0.38";

// src/cli.ts
var cli = cac("hattip-deno");
cli.command(
  "<input> <output>",
  "Bundle the HatTip app in <input> into <output> as a Deno module"
).option(
  "-s, --staticDir <dir>",
  "Static files directory to copy next to the output"
).option(
  "-n, --nodeCompat",
  "Enable Node.js compatibility (e.g. polyfilling Node.js globals)",
  { default: false }
).action(
  async (input, output, { staticDir, nodeCompat }) => {
    await bundle({ input, output, staticDir, nodeCompat });
  }
);
cli.help();
cli.version(version);
cli.parse();
